def count_handles(handles)
    return handles.length
  end
  
  def shortest_handle(handles)
    return handles.min_by { |handle| handle.length }
  end
  
  def count_handles_with_length(handles, length)
    return handles.count { |handle| handle.length - 1 == length }
  end
  
  def count_capitalized_handles(handles)
    return handles.count { |handle| /[A-Z]/.match?(handle[1]) }
  end
  
  def sort_handles_alphabetically(handles)
    return handles.sort
  end
  
  def sort_handles_by_length(handles)
    return handles.sort_by { |handle| handle.length }
  end
  
  def find_handle_position(handles, target_handle)
    return handles.index(target_handle)
  end
  
  def distribution_by_length(handles)
    distribution = Hash.new(0)
    handles.each { |handle| distribution[handle.length - 1] += 1 }
    return distribution
  end
  
  def display_menu
    puts "Menu :"
    puts "1. Combien y a-t-il de handle dans la liste ?"
    puts "2. Quel est le handle le plus court de la liste ?"
    puts "3. Combien y a-t-il de handle contenant un certain nombre de caractères ?"
    puts "4. Combien de handles commencent par une majuscule ?"
    puts "5. Trier la liste de handles par ordre alphabétique."
    puts "6. Trier la liste de handles par taille des handles (les plus petits en premiers, les plus grands après)."
    puts "7. Quelle est la position dans la liste de la personne @epenser ?"
    puts "8. Afficher la répartition des handles par taille."
    puts "9. Quitter."
  end
  
